"use client"

import { useState } from "react"
import { useRouter } from "next/navigation"
import { Button } from "@/components/ui/button"
import { Loader2, UploadCloud } from "lucide-react"

export default function DetectPage() {
  const [selectedFile, setSelectedFile] = useState<File | null>(null)
  const [previewURL, setPreviewURL] = useState<string | null>(null)
  const [loading, setLoading] = useState(false)
  const [result, setResult] = useState<string | null>(null)

  const handleFileChange = (e) => {
    const file = e.target.files?.[0]
    if (file) {
      setSelectedFile(file)
      setPreviewURL(URL.createObjectURL(file))
      setResult(null)
    }
  }

  const handleSubmit = async () => {
    if (!selectedFile) return
    setLoading(true)

    const reader = new FileReader()
    reader.onloadend = async () => {
      const base64String = reader.result ? reader.result.toString() : ""
      const payload = {
        image: [base64String]
      }

      try {
        const response = await fetch("http://localhost:5000/", {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify(payload)
        })

        const data = await response.json()
        setResult(`Prediction Score: ${(data.result[0] * 100).toFixed(2)}%`)
      } catch (error) {
        console.error("Prediction error:", error)
        setResult("Something went wrong.")
      } finally {
        setLoading(false)
      }
    }

    reader.readAsDataURL(selectedFile)
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-slate-950 to-slate-900 text-white p-6 flex flex-col items-center justify-center">
      <h1 className="text-3xl font-bold mb-6 text-center">Upload MRI Scan for Tumor Detection</h1>

      <input
        type="file"
        accept="image/*"
        onChange={handleFileChange}
        className="mb-4"
      />

      {previewURL && (
        <img
          src={previewURL}
          alt="Preview"
          className="w-64 h-64 object-cover rounded-xl shadow-lg mb-4"
        />
      )}

      <Button
        disabled={!selectedFile || loading}
        onClick={handleSubmit}
        className="bg-gradient-to-r from-cyan-500 to-blue-600 hover:from-cyan-600 hover:to-blue-700 text-white font-semibold px-6 py-3 rounded-full"
      >
        {loading ? (
          <>
            <Loader2 className="animate-spin mr-2" /> Analyzing...
          </>
        ) : (
          <>
            <UploadCloud className="mr-2" /> Start Detection
          </>
        )}
      </Button>

      {result && (
        <p className="mt-6 text-xl text-cyan-300 font-medium">{result}</p>
      )}
    </div>
  )
}
