import { ThemeProvider } from "styled-components";
import { useState, useEffect } from "react";
import styled from "styled-components";
import ImageUpload from "./Components/ImageUpload";
import ImagesCard from "./Components/ImagesCard";
import Loader from "./Components/Loader/Loader";
import ResultCard from "./Components/ResultCard";
import axios from "axios";
import { Images } from "./data";
import { darkTheme } from "./utils/themes"; // Re-added the import

const Body = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  width: 100vw;
  min-height: 100vh;
  background-color: ${({ theme }) => theme.bg};
  font-family: "Inter", sans-serif; /* Modern, medical font */
  overflow-y: scroll;
  padding: 20px 0;
`;

const Heading = styled.div`
  font-size: 44px;
  font-weight: 700;
  color: ${({ theme }) => theme.text};
  margin: 3% 0;
  letter-spacing: 1px;
  text-transform: uppercase;
  position: relative;
  padding-bottom: 10px;
  &:after {
    content: "";
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background-color: ${({ theme }) => theme.primary};
  }
  @media (max-width: 530px) {
    font-size: 32px;
  }
`;

const Container = styled.div`
  max-width: 100%;
  display: flex;
  justify-content: center;
  flex-direction: row;
  gap: 50px;
  padding: 3% 5% 7% 5%;
  @media (max-width: 1100px) {
    flex-direction: column;
    align-items: center;
  }
`;

const Centered = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const FlexItem = styled.div`
  width: 500px;
  display: flex;
  flex-direction: column;
  gap: 35px;
  flex: 1;
  @media (max-width: 530px) {
    width: 400px;
  }
  @media (max-width: 430px) {
    width: 300px;
  }
`;

const TextCenter = styled.div`
  font-size: 20px;
  font-weight: 500;
  color: ${({ theme }) => theme.text};
  text-align: center;
  letter-spacing: 0.5px;
  background: linear-gradient(
    90deg,
    transparent,
    ${({ theme }) => theme.soft2}20,
    transparent
  );
  padding: 8px 0;
`;

const SelectedImages = styled.div`
  display: grid;
  grid-template-columns: auto auto auto;
  justify-content: center;
  gap: 12px;
  align-items: center;
  @media (max-width: 530px) {
    grid-template-columns: auto auto;
  }
`;

const Button = styled.button`
  min-height: 52px;
  border-radius: 10px;
  background: linear-gradient(
    135deg,
    ${({ theme }) => theme.primary} 0%,
    ${({ theme }) => theme.primary}cc 100%
  );
  color: #ffffff;
  font-weight: 600;
  font-size: 16px;
  font-family: "Inter", sans-serif;
  border: 1px solid ${({ theme }) => theme.primary}33;
  cursor: pointer;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 28px;
  box-shadow: 0 4px 8px ${({ theme }) => theme.shadow || "rgba(0, 0, 0, 0.2)"};
  transition: transform 0.2s ease, box-shadow 0.2s ease;
  text-transform: uppercase;
  letter-spacing: 0.8px;
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 12px ${({ theme }) => theme.shadow || "rgba(0, 0, 0, 0.3)"};
    background: linear-gradient(
      135deg,
      ${({ theme }) => theme.primary} 0%,
      ${({ theme }) => theme.primary} 100%
    );
  }
  &:active {
    transform: translateY(0);
    box-shadow: 0 2px 4px ${({ theme }) => theme.shadow || "rgba(0, 0, 0, 0.2)"};
  }
  &:disabled {
    background: ${({ theme }) => theme.soft2};
    cursor: not-allowed;
    box-shadow: none;
  }
`;

const Typo = styled.div`
  font-size: 26px;
  font-weight: 600;
  color: ${({ theme }) => theme.text};
  padding-left: 12px;
  border-left: 4px solid ${({ theme }) => theme.primary};
  letter-spacing: 0.5px;
`;

const ResultWrapper = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

function App() {
  const [images, setImages] = useState(null);
  const [predictedImage, setPredictedImage] = useState(null);
  const [predictions, setPredictions] = useState();
  const [loading, setLoading] = useState(false);
  const [showPrediction, setShowPrediction] = useState(false);

  const generatePrediction = async () => {
    setLoading(true);
    const imageData = [];
    for (let i = 0; i < images.length; i++) {
      imageData.push(images[i].base64_file);
    }
    const data = { image: imageData };
    try {
      const res = await axios.post("http://localhost:5000/", data);
      setPredictedImage(images);
      setPredictions({ image: imageData, result: res.data.result });
      setShowPrediction(true);
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  const generateNewImages = () => {
    const newImages = [];
    for (let i = 0; i < 3; i++) {
      const randomIndex = Math.floor(Math.random() * Images.length);
      newImages.push({
        base64_file: Images[randomIndex],
        file_name: `Sample ${i + 1}`,
      });
    }
    setImages(newImages);
  };

  useEffect(() => {
    generateNewImages();
  }, []);

  return (
    <ThemeProvider theme={darkTheme}>
      <Body>
        <Heading>Brain Tumor Analysis ð§ </Heading>
        {loading ? (
          <Centered>
            <Loader />
          </Centered>
        ) : (
          <Container>
            <FlexItem>
              <ImageUpload images={images} setImages={setImages} />
              <TextCenter>Or Analyze Sample MRI Scans</TextCenter>
              <SelectedImages>
                {images &&
                  images.map((image, index) => (
                    <ImagesCard key={index} image={image} />
                  ))}
              </SelectedImages>
              <Button onClick={generateNewImages}>Load Sample Scans</Button>
              {images && (
                <Button onClick={generatePrediction} disabled={loading}>
                  Analyze Scans
                </Button>
              )}
            </FlexItem>
            {showPrediction && (
              <FlexItem>
                <Typo>Analysis Results</Typo>
                <ResultWrapper>
                  {predictedImage.map((image,Ã©ia) => (
                    <ResultCard
                      key={index}
                      image={image}
                      prediction={predictions.result[index]}
                    />
                  ))}
                </ResultWrapper>
              </FlexItem>
            )}
          </Container>
        )}
      </Body>
    </ThemeProvider>
  );
}

export default App;
