import React, { useState, useEffect } from "react";
import styled from 'styled-components';
import ImageUpload from "./ImageUpload";
import ImagesCard from "./ImagesCard";
import Loader from "./Loader/Loader";
import ResultCard from "./ResultCard";
import axios from 'axios';
import { Images } from "../data";

const Body = styled.div`
  display: flex; 
  align-items: center;
  flex-direction: column;
  width: 100vw;
  min-height: 100vh;
  background: linear-gradient(135deg, #0a192f 0%, #112240 100%);
  overflow-y: scroll;
`;

const Heading = styled.div`
  font-size: 42px;
  @media (max-width: 530px) {
    font-size: 30px
  }
  font-weight: 600;
  color: #64ffda;
  margin: 2% 0px;
`;

const Container = styled.div`
  max-width: 100%;
  display: flex; 
  justify-content: center;
  flex-direction: row;
  @media (max-width: 1100px) {
    flex-direction: column;
  }
  gap: 40px;
  padding: 2% 0% 6% 0%;
`;

const Centered = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const FlexItem = styled.div`
  width: 500px;
  @media (max-width: 530px) {
    width: 400px
  }
  @media (max-width: 430px) {
    width: 300px
  }
  display: flex;
  flex-direction: column;
  gap: 40px;
  flex: 1;
`;

const TextCenter = styled.div`
  font-size: 22px;
  font-weight: 600;
  color: #8892b0;
  text-align: center;
`;

const SelectedImages = styled.div`
  display: grid;
  grid-template-columns: auto auto auto;
  @media (max-width: 530px) {
    grid-template-columns: auto auto;
  }
  justify-content: center;
  gap: 10px;
  align-items: center;
`;

const Button = styled.div`
  min-height: 48px;
  border-radius: 8px;
  background: linear-gradient(120deg, #64ffda, #48b1bf);
  color: #0a192f;
  margin: 3px 20px;
  font-weight: 600;
  font-size: 16px;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0px 14px;
  cursor: pointer;
  transition: transform 0.3s ease;
  
  &:hover {
    transform: translateY(-3px);
  }
`;

const Typo = styled.div`
  font-size: 24px;
  font-weight: 600;
  color: #64ffda;
`;

const ResultWrapper = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px;
`;

const Detection = () => {
  const [images, setImages] = useState(null);
  const [predictedImage, setPredictedImage] = useState(null);
  const [predictions, setPredictions] = useState();
  const [loading, setLoading] = useState(false);
  const [showPrediction, setShowPrediction] = useState(false);

  const generatePrediction = async () => {
    setLoading(true);
    const imageData = []
    for (let i = 0; i < images.length; i++) {
      imageData.push(images[i].base64_file)
    }
    const data = { image: imageData }
    const res = await axios.post('http://localhost:5000/', data).catch((err) => {
      console.log(err);
    });
    setPredictedImage(images)
    setPredictions({ image: imageData, result: res.data.result })
    setShowPrediction(true);
    setLoading(false);
  }

  const generateNewImages = () => {
    const newImages = [];
    for (let i = 0; i < 3; i++) {
      const randomIndex = Math.floor(Math.random() * Images.length);
      newImages.push({
        base64_file: Images[randomIndex],
        file_name: `Sample ${i + 1}`,
      });
    }
    setImages(newImages);
  };

  useEffect(() => {
    generateNewImages();
  }, []);

  return (
    <Body>
      <Heading>Brain Tumor Detector ðŸ§ </Heading>
      {loading ?
        <Centered>
          <Loader />
        </Centered>
        :
        <Container>
          <FlexItem>
            <ImageUpload images={images} setImages={setImages} />
            <TextCenter>Or try with sample data</TextCenter>
            <SelectedImages>
              {images && images.map((image, index) => {
                return (
                  <ImagesCard
                    key={index}
                    image={image}
                  />
                );
              })}
            </SelectedImages>
            <Button onClick={() => generateNewImages()}>Get Sample Images</Button>
            {images &&
              <Button onClick={() => { generatePrediction() }}>PREDICT</Button>}
          </FlexItem>
          {showPrediction &&
            <FlexItem style={{ gap: '22px' }}>
              <Typo>Our Predictions</Typo>
              <ResultWrapper>
                {predictedImage.map((image, index) => {
                  return (
                    <ResultCard
                      key={index}
                      image={image}
                      prediction={predictions.result[index]}
                    />
                  );
                })}
              </ResultWrapper>
            </FlexItem>
          }
        </Container>
      }
    </Body>
  );
};

export default Detection;
