import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

const LandingContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #0a192f 0%, #112240 100%);
  color: #fff;
  padding: 2rem;
`;

const Hero = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 80vh;
  text-align: center;
`;

const Title = styled.h1`
  font-size: 4rem;
  margin-bottom: 1rem;
  background: linear-gradient(120deg, #64ffda, #48b1bf);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`;

const Subtitle = styled.p`
  font-size: 1.5rem;
  margin-bottom: 2rem;
  color: #8892b0;
  max-width: 600px;
`;

const StartButton = styled.button`
  background: linear-gradient(120deg, #64ffda, #48b1bf);
  border: none;
  padding: 1rem 2rem;
  font-size: 1.2rem;
  color: #0a192f;
  border-radius: 5px;
  cursor: pointer;
  transition: transform 0.3s ease;
  
  &:hover {
    transform: translateY(-3px);
  }
`;

const TestimonialsSection = styled.div`
  padding: 4rem 0;
`;

const TestimonialsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const TestimonialCard = styled.div`
  background: rgba(17, 34, 64, 0.5);
  padding: 2rem;
  border-radius: 10px;
  border: 1px solid #64ffda;
`;

const TestimonialText = styled.p`
  color: #8892b0;
  font-style: italic;
  margin-bottom: 1rem;
`;

const TestimonialAuthor = styled.p`
  color: #64ffda;
  font-weight: bold;
`;

const testimonials = [
  {
    text: "This AI-powered brain tumor detection system has revolutionized our diagnostic process. The accuracy is remarkable!",
    author: "Dr. Sarah Johnson, Neurologist"
  },
  {
    text: "As a medical researcher, I'm impressed by the advanced technology and ease of use. It's a game-changer in medical imaging.",
    author: "Prof. Michael Chen, Research Director"
  },
  {
    text: "The system's ability to detect tumors early has helped us save countless lives. It's an invaluable tool for modern medicine.",
    author: "Dr. Emily Rodriguez, Radiologist"
  }
];

const Landing = () => {
  const navigate = useNavigate();

  return (
    <LandingContainer>
      <Hero>
        <Title>Brain Tumor Detection</Title>
        <Subtitle>
          Advanced AI-powered system for early and accurate brain tumor detection.
          Using cutting-edge machine learning to assist medical professionals.
        </Subtitle>
        <StartButton onClick={() => navigate('/detect')}>
          Start Detection Now
        </StartButton>
      </Hero>
      
      <TestimonialsSection>
        <TestimonialsGrid>
          {testimonials.map((testimonial, index) => (
            <TestimonialCard key={index}>
              <TestimonialText>"{testimonial.text}"</TestimonialText>
              <TestimonialAuthor>- {testimonial.author}</TestimonialAuthor>
            </TestimonialCard>
          ))}
        </TestimonialsGrid>
      </TestimonialsSection>
    </LandingContainer>
  );
};

export default Landing;
