import { ThemeProvider } from "styled-components";
import { useState, useEffect } from "react";
import styled from "styled-components";
import ImageUpload from "./Components/ImageUpload";
import ImagesCard from "./Components/ImagesCard";
import Loader from "./Components/Loader/Loader";
import ResultCard from "./Components/ResultCard";
import axios from "axios";
import { Images } from "./data";

// Medical-inspired theme
const medicalTheme = {
  bg: "#F5F7FA", // Soft white-gray background
  text: "#2D3748", // Dark gray for readability
  primary: "#0073E6", // Clinical blue for buttons
  secondary: "#E6F0FA", // Light blue for accents
  cardBg: "#FFFFFF", // White for cards
  border: "#D1D9E6", // Subtle gray-blue borders
  shadow: "rgba(0, 0, 0, 0.05)",
  softText: "#6B7280", // Muted gray for secondary text
};

const Body = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  width: 100vw;
  min-height: 100vh;
  background-color: ${({ theme }) => theme.bg};
  font-family: "Roboto", sans-serif; /* Clean, professional font */
  overflow-y: auto;
  padding: 20px;
`;

const Heading = styled.div`
  font-size: 36px;
  font-weight: 700;
  color: ${({ theme }) => theme.text};
  margin: 30px 0;
  text-align: center;
  letter-spacing: 0.5px;
  @media (max-width: 530px) {
    font-size: 28px;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  width: 100%;
  display: flex;
  justify-content: center;
  flex-direction: row;
  gap: 60px;
  padding: 40px 20px;
  @media (max-width: 1100px) {
    flex-direction: column;
    align-items: center;
  }
`;

const Centered = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: ${({ theme }) => theme.bg};
`;

const FlexItem = styled.div`
  max-width: 550px;
  width: 100%;
  display: flex;
  flex-direction: column;
  gap: 30px;
  flex: 1;
  background-color: ${({ theme }) => theme.cardBg};
  padding: 30px;
  border-radius: 12px;
  box-shadow: 0 4px 12px ${({ theme }) => theme.shadow};
  @media (max-width: 530px) {
    padding: 20px;
  }
`;

const TextCenter = styled.div`
  font-size: 18px;
  font-weight: 500;
  color: ${({ theme }) => theme.softText};
  text-align: center;
`;

const SelectedImages = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
  gap: 15px;
  justify-content: center;
  align-items: center;
`;

const Button = styled.button`
  min-height: 50px;
  border-radius: 8px;
  background-color: ${({ theme }) => theme.primary};
  color: #ffffff;
  font-weight: 600;
  font-size: 16px;
  border: none;
  cursor: pointer;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 24px;
  transition: background-color 0.2s ease;
  &:hover {
    background-color: #005BB5; /* Darker blue on hover */
  }
  &:disabled {
    background-color: ${({ theme }) => theme.border};
    cursor: not-allowed;
  }
`;

const Typo = styled.div`
  font-size: 22px;
  font-weight: 600;
  color: ${({ theme }) => theme.text};
  border-left: 4px solid ${({ theme }) => theme.primary};
  padding-left: 12px;
`;

const ResultWrapper = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

function App() {
  const [images, setImages] = useState(null);
  const [predictedImage, setPredictedImage] = useState(null);
  const [predictions, setPredictions] = useState();
  const [loading, setLoading] = useState(false);
  const [showPrediction, setShowPrediction] = useState(false);

  const generatePrediction = async () => {
    setLoading(true);
    const imageData = [];
    for (let i = 0; i < images.length; i++) {
      imageData.push(images[i].base64_file);
    }
    const data = { image: imageData };
    try {
      const res = await axios.post("http://localhost:5000/", data);
      setPredictedImage(images);
      setPredictions({ image: imageData, result: res.data.result });
      setShowPrediction(true);
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  const generateNewImages = () => {
    const newImages = [];
    for (let i = 0; i < 3; i++) {
      const randomIndex = Math.floor(Math.random() * Images.length);
      newImages.push({
        base64_file: Images[randomIndex],
        file_name: `Sample ${i + 1}`,
      });
    }
    setImages(newImages);
  };

  useEffect(() => {
    generateNewImages();
  }, []);

  return (
    <ThemeProvider theme={medicalTheme}>
      <Body>
        <Heading>Brain Tumor Analysis System ðŸ§ </Heading>
        {loading ? (
          <Centered>
            <Loader />
          </Centered>
        ) : (
          <Container>
            <FlexItem>
              <ImageUpload images={images} setImages={setImages} />
              <TextCenter>Or analyze sample MRI scans</TextCenter>
              <SelectedImages>
                {images &&
                  images.map((image, index) => (
                    <ImagesCard key={index} image={image} />
                  ))}
              </SelectedImages>
              <Button onClick={generateNewImages}>Load Sample Scans</Button>
              {images && (
                <Button onClick={generatePrediction} disabled={loading}>
                  Analyze Scans
                </Button>
              )}
            </FlexItem>
            {showPrediction && (
              <FlexItem>
                <Typo>Analysis Results</Typo>
                <ResultWrapper>
                  {predictedImage.map((image, index) => (
                    <ResultCard
                      key={index}
                      image={image}
                      prediction={predictions.result[index]}
                    />
                  ))}
                </ResultWrapper>
              </FlexItem>
            )}
          </Container>
        )}
      </Body>
    </ThemeProvider>
  );
}

export default App;
